import React from 'react';
import PropTypes from 'prop-types';

/**
 * Hasta la version 16.8 -> Class Component - Statefull components
 * Desde la version 16.8 -> Funtional Components - Statelles components (Hacks)
 */

const Banner = ({ value }) => {
  return (
    <div>
      <h1>CounterApp</h1>
      <h2>{value}</h2>
      <button onClick={(e) => handle(e)}>+1</button>
    </div>
  );
};

Banner.prototype = {
  title: PropTypes.number.isRequire
};

export default Banner;
