import React, { useState } from 'react';
import PropTypes from 'prop-types';

/**
 * Hasta la version 16.8 -> Class Component - Statefull components
 * Desde la version 16.8 -> Funtional Components - Statelles components (Hacks)
 */
const VALOR_INICIAL = 10;
const CounterApp = ({ value }) => {
  const [counter, setCounter] = useState({
    counter1: VALOR_INICIAL,
    counter2: 20
  });

  const {counter1, counter2} = counter;
  const handleAdd2 = () => {
    setCounter({
      ...counter,
      counter1: counter1 + 1
    })
  };

  const handleAdd = () => {
    setCounter({
      counter1: counter1 + 1,
      counter2: counter2
    })
  };

  const handleSubstract = () => {
    setCounter({
      counter1: counter1 - 1,
      counter2 : counter2})
  };
  const handleReset = () => {
    setCounter({
      counter1: 0,
      counter2: counter2
    })
  };



  return (
    <div>
      <h1>CounterApp</h1>
      <h2>{counter1}</h2>
      <h2>{counter2}</h2>

      <button onClick={(e) => handleAdd(e)}>+1</button>
      <button onClick={(e) => handleReset(e)}>Reset</button>
      <button onClick={(e) => handleSubstract(e)}>-1</button>



    </div>
  );
};

CounterApp.prototype = {
  title: PropTypes.number.isRequire
};

export default CounterApp;
