import React from 'react';
import Banner from './components/Banner/Banner';

function App() {
  const handClick = (e) => {
    console.log(e);
    console.log('No me toques');
  };

  const mouseOver = (e) => {
    // console.log('mouseOver');
    e.target.style.background = 'pink';
  };

  const mouseOut = (e) => {
    // console.log('mouseOut');
    e.target.style.background = 'pink';
  };

  return (
    <>
      <Banner
        title="Events"
        onClick={handClick}
        mouseOut={mouseOut}
        mouseOver={mouseOver}
      />
    </>
  );
}

export default App;
