import React, {useState} from 'react';
import Form from './components/Formulario';

function App() {

  const [user, setUser] = useState({});

  const onSubmit = (data) => {
    for(const key in data){
      console.log(`{key}: ${data[key]}`);
    }

    setUser(data);
  }

  return (
    <div className="container mt-4">
      <Form onSubmit={onSubmit}/>
      <Modal data={user}/>
    </div>
  );
}

export default App;
