import React,{useState,useEffect} from 'react';
import PropTypes from 'prop-types';

import { v4 as uuidv4 } from 'uuid';

const initialsPost = [
    { id: uuidv4(), authorPost: 'Alexis', textPost: '-0,25 Por no poner bien el padding.' },
    { id: uuidv4(), authorPost: 'Jose Carlos', textPost: 'La próxima te quito 1.5 por no poner en rojo el color.' }
  ];



const FormSendPost = () => {

    const doGetPosts = () => {
        const result = JSON.parse(localStorage.getItem('post'));
        return result ? result : initialsPosts;
    };

    const [categories, setCategories] = useState(doGetCategories());


    const handleAdd = () => {
      setCategories([
        ...categories,
        { id: uuidv4(), name: 'Tarjetas de memoria' }
      ]);
      localStorage.setItem('categories', JSON.stringify(categories));
    };

    const handleDelete = (id) => {
          setCategories(categories.filter((category) => category.id !== id));
        };

    // [] -> Si pongo un Array vacío se ejecutará una única vez, al cargar el componente.
    // Nada -> Se ejecutará en cada renderizado del componente.
    // [estado] -> Se ejecutará en cada cambio que sufra el estado.
    useEffect(() => {
      localStorage.setItem('categories', JSON.stringify(categories));
    }, []); 


    return (
            <div className=" col-6 sinPading">
                <div className="card">
                    <div className="card-header colorAutor"> Autor: </div>
                    <div className="card-body">
                    <label className="form-label" id="title">Título: </label>
                    <input id="title" type="text" className="form-control"/>
                    <label className="form-label" id="message">Mensaje: </label>
                    <input id="message" type="text" className="form-control" placeholder="Tu texto..."/>
                    <button type="submit" className="btn btn-primary">Nuevo post</button>
                    </div>
                </div>
            </div>
    );
};

FormSendPost.propTypes = {
    input: PropTypes.string.isRequired
};

export default FormSendPost;
